script:
frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        var company = frm.doc.company;

        if (company == "SANTOSH UNIT 2") {

            frm.set_df_property('plant', 'options', ['Pipe Plant', 'Tundish Plant', 'Buluster Plant']);
        } else if (company == "Khetan Udyog") {

            frm.set_df_property('plant', 'options', ['Telescopic Plant']);
        }
    },
    company: function(frm){
        var company = frm.doc.company;

        if (company == "SANTOSH UNIT 2") {

            frm.set_df_property('plant', 'options', ['Pipe Plant', 'Tundish Plant', 'Buluster Plant']);

        } else if (company == "Khetan Udyog") {

            frm.set_df_property('plant', 'options', ['Telescopic Plant']);
        }
        else{
            frm.set_df_property('plant', 'options', ['Pipe Plant', 'Tundish Plant', 'Buluster Plant','Telescopic Plant']);
        }
        frm.refresh_field('plant');
    }   
});
